<?phprequire_once('Smarty.class.php');interface smartyParas{	public function parse($tpl_var, $resource_name);}class SmartyEx extends Smarty implements smartyParas{	public function parse($tpl_var, $resource_name){	$this->_tpl_vars[$tpl_var] = $this->fetch($resource_name);	}}$smarty = new SmartyEx; $smarty->template_dir = 'templates';$smarty->config_dir = 'Smarty/config';$smarty->cache_dir = 'smarty/cache'; $smarty->compile_dir = 'smarty/templates_c';$smarty->right_delimiter="}}";$smarty->left_delimiter="{{";?>